# Dados de exemplo para imagens em base64
# Estas imagens são usadas como fallback caso as URLs remotas não funcionem

def get_placeholder_image_dict():
    """
    Retorna um dicionário com imagens em base64 para cada categoria
    
    Returns:
        dict: Dicionário contendo imagens em base64 por categoria
    """
    placeholder_images = {
        "legal": "data:image/svg+xml;base64,",
        "medical": "data:image/svg+xml;base64,",
        "doctor_patient": "data:image/svg+xml;base64,",
        "law_office": "data:image/svg+xml;base64,",
        "cover": "data:image/svg+xml;base64,",
        "author": "data:image/svg+xml;base64,"
    }
    return placeholder_images